import styled from '@emotion/styled';

export const Title = styled.div`

    display: flex;
    flex-direction: column;
    align-items: center;

    font-size: 20px;
    font-weight: 700;
    margin-bottom: 20px;
`;

export const Avatar = styled.img`
    width: 100px;
    height: 100px;
    border-radius: 50%;
    margin-bottom: 20px;
`;
