import styled from '@emotion/styled';

export const Title = styled.section`
font-family: 'Roboto', sans-serif;
font-weight: 700;
font-size: 16px;
color: #3d3d3d;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
padding: 0px;
background-color: #fff;
border-radius: 5px;
box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
min-width: 400px;
margin-bottom: 20px;
`;

export const StatList = styled.h2`
    font-size: 25px;
    font-weight: 700;
    color: grey;
    display: flex;
    justify-content: center;
    align-items: center;
    min-width: 100%;
    margin: 0;
    padding: 20px 0px 20px 0px;
    box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
    }
`;


export const Item = styled.li`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    margin: 0;
    padding: 20px 0px 20px 0px;
    box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
    }
`;

export const Label = styled.ul`
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    padding: 0;
    margin: 0;
    background-color: #79F2FF;
    }
`;
