import styled from '@emotion/styled';

export const Title = styled.section`
font-family: 'Roboto', sans-serif;
font-weight: 700;
font-size: 16px;
color: #3d3d3d;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
padding: 20px;
background-color: #fff;
border-radius: 5px;
box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
min-width: 400px;
margin-bottom: 20px;
padding: 20px;
`;
