import styled from '@emotion/styled';

export const Title = styled.section`
font-family: 'Roboto', sans-serif;
font-weight: 700;
font-size: 16px;
color: #3d3d3d;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
padding: 20px;
background-color: #fff;
border-radius: 5px;
box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
min-width: 400px;
margin-bottom: 20px;
padding: 20px;
`;

export const StatList = styled.h2`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    margin-bottom: 20px;
    margin-left: 20px;
    margin-right: 20px;
    padding: 20px;
    border: 2px solid #fff;
    box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
    transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);
    &:hover,
    &:focus {
        transform: scale(1.2);
        border-color: #ff6b08;
    }
`;
