export const User = ({user: {name, tag, location, avatar, stats: {followers, views, likes}
}}) => {
    return (
            <div>
            <div>
                <img src={avatar } alt={user.tag} />
            <p>{user.username}</p>
            <p>@{user.tag}</p>
            <p>{user.location}</p>
            </div>

            <ul>
            <li>
                <span>Followers</span>
                <span>
                {user.stats.followers}
                </span>
            </li>
            <li>
                <span>Views</span>
                <span>
                {user.stats.views}
                </span>
            </li>
            <li>
                <span>Likes</span>
                <span>
                {user.stats.likes}
                </span>
            </li>
            </ul>
        </div>
    )
};
